mapper.select:
SELECT * FROM board b
LEFT JOIN user u ON u.user_id = #{user_id}
WHERE b.user_id = #{user_id} OR b.board_share LIKE CONCAT("%", #{user_id}, "%")

SELECT count(*) FROM board
WHERE user_group = #{user_group}


		AND #{search_type} LIKE CONCAT('%', #{keyword}, '%')

SELECT user_id, user_name_k
FROM user
WHERE user_group = #{user_group} OR user_id = #{user_id}

SELECT * FROM board b
LEFT JOIN user u ON b.user_id = u.user_id
WHERE b.board_id = #{board_id}

mapper.update:
UPDATE board 
SET board_title= #{board_title}, board_start = #{board_start}, board_end = #{board_end}, board_content = #{board_content}, board_share = #{board_share}, modify_date = NOW()
WHERE board_id = #{board_id}

mapper.select:
/* listResultReport */
	SELECT
		*
	FROM
		report
	group by report_code;

SELECT p.*,r.*,s.*,m.*,rp.*,wsi.*,u.user_name_k FROM plan p LEFT JOIN report r ON p.report_code = r.report_code LEFT JOIN storage s ON p.storage_id = s.storage_id LEFT JOIN method m ON p.plan_method = m.method_id LEFT JOIN result_plan rp on p.plan_id = rp.plan_id LEFT JOIN weather_soil_info wsi on p.plan_id = wsi.plan_id LEFT JOIN user u on u.user_id = rp.user_id WHERE rp_id is not null
and r.report_code = #{report_code}
and p.user_group = #{user_group}
and rp_id = #{rp_id}
limit #{offset},#{limit}
