mapper.select:
SELECT m.*, u.* FROM material m LEFT JOIN user u ON m.user_id = u.user_id WHERE m.user_group = #{user_group}
AND (material_code LIKE CONCAT('%', #{keyword}, '%') OR material_name LIKE CONCAT('%', #{keyword}, '%'))
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY material_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM material m LEFT JOIN user u ON m.user_id = u.user_id WHERE m.user_group = #{user_group}
AND (material_code LIKE CONCAT('%', #{keyword}, '%') OR material_name LIKE CONCAT('%', #{keyword}, '%'))
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE material
SET material_name = #{material_name}, modify_date = NOW()
WHERE material_id = #{material_id}
