mapper.select:
SELECT * FROM report_file r LEFT JOIN user u ON r.user_id = u.user_id
WHERE #{search_type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY r.report_file_id DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE report_file
SET report_contents = #{report_contents}, modify_date = NOW()
WHERE report_file_id = #{report_file_id}
