mapper.select:
SELECT p.*, r.*, s.*, m.*, r.user_name AS report_user_name, rf.report_year FROM plan p LEFT JOIN report r ON p.report_code = r.report_code LEFT JOIN report_file rf ON r.report_file_id = rf.report_file_id LEFT JOIN storage s ON p.storage_id = s.storage_id LEFT JOIN method m ON p.plan_method = m.method_id WHERE p.user_group = #{user_group}
AND ( p.plan_code LIKE CONCAT('%', #{keyword}, '%') OR r.report_title LIKE CONCAT('%', #{keyword}, '%') OR s.storage_name LIKE CONCAT('%', #{keyword}, '%') OR m.method_title LIKE CONCAT('%', #{keyword}, '%') OR p.plan_repeat = #{keyword} OR p.plan_segment = #{keyword} )
AND ${search_type} = #{keyword}
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY plan_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM plan p LEFT JOIN report r ON p.report_code = r.report_code LEFT JOIN report_file rf ON r.report_file_id = rf.report_file_id LEFT JOIN storage s ON p.storage_id = s.storage_id LEFT JOIN method m ON p.plan_method = m.method_id WHERE p.user_group = #{user_group}
AND ( p.plan_code LIKE CONCAT('%', #{keyword}, '%') OR r.report_title LIKE CONCAT('%', #{keyword}, '%') OR s.storage_name LIKE CONCAT('%', #{keyword}, '%') OR m.method_title LIKE CONCAT('%', #{keyword}, '%') OR p.plan_repeat = #{keyword} OR p.plan_segment = #{keyword} )
AND ${search_type} = #{keyword}
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')

SELECT p.*, r.*, s.*, m.* FROM plan p LEFT JOIN report r ON p.report_code = r.report_code LEFT JOIN storage s ON p.storage_id = s.storage_id LEFT JOIN method m ON p.plan_method = m.method_id WHERE p.user_group = #{user_group} AND p.plan_step = #{plan_step}
AND ( p.plan_code LIKE CONCAT('%', #{keyword}, '%') OR r.report_title LIKE CONCAT('%', #{keyword}, '%') OR s.storage_name LIKE CONCAT('%', #{keyword}, '%') OR m.method_title LIKE CONCAT('%', #{keyword}, '%') OR p.plan_repeat = #{keyword} OR p.plan_segment = #{keyword} )
AND ${search_type} = #{keyword}
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY plan_id DESC LIMIT #{offset}, #{limit}

