SELECT * FROM fertilizer
WHERE fert_depth = #{fert_depth}


		AND fert_parent = #{fert_id}

SELECT * FROM factor
WHERE plan_id = #{plan_id}
ORDER BY factor_index

SELECT * FROM manure
WHERE plan_id = #{plan_id}
ORDER BY manure_level, manure_id

SELECT * FROM etc
WHERE plan_id = #{plan_id}
ORDER BY etc_id

SELECT * FROM record
WHERE record_type = #{record_type}
ORDER BY record_id DESC

mapper.update:
