SELECT r.*, u.*, e.eaches_name FROM reagent r LEFT JOIN eaches e ON r.reagent_id = e.eaches_id LEFT JOIN user u ON r.user_id = u.user_id WHERE r.user_group = #{user_group}
AND ( r.reagent_code LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_type LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_name LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_standard LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_nick_e LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_nick_k LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_manufacturer LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_quantity = #{keyword} )
AND ${search_type} = #{keyword}
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY reagent_id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM reagent r LEFT JOIN eaches e ON r.reagent_id = e.eaches_id LEFT JOIN user u ON r.user_id = u.user_id WHERE r.user_group = #{user_group}
AND ( r.reagent_code LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_type LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_name LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_standard LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_nick_e LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_nick_k LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_manufacturer LIKE CONCAT('%', #{keyword}, '%') OR r.reagent_quantity = #{keyword} )
AND ${search_type} = #{keyword}
AND ${search_type} LIKE CONCAT('%', #{keyword}, '%')
